import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import {
  Box,
  Button,
  Typography,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  IconButton,
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import SettingsIcon from '@mui/icons-material/Settings';
import WaterDropIcon from '@mui/icons-material/WaterDrop';

const GlassOfWaterIcon = ({ progress }) => {
  return (
    <svg viewBox="0 0 24 24" style={{ width: '50px', height: '50px', fill: 'none' }}>
      <rect x="7" y="2" width="10" height="20" rx="2" ry="2" stroke="black" strokeWidth="1" fill="none" />
      <rect
        x="7"
        y={2 + 20 - (progress / 100) * 20}
        width="10"
        height={(progress / 100) * 20}
        fill="#94C1C6"
        stroke="none"
      />
    </svg>
  );
};

GlassOfWaterIcon.propTypes = {
  progress: PropTypes.number.isRequired,
};

const WaterIntake = () => {
  const [goal, setGoal] = useState(5000);
  const [intake, setIntake] = useState(13750);
  const [goalDialogOpen, setGoalDialogOpen] = useState(false);
  const [intakeDialogOpen, setIntakeDialogOpen] = useState(false);
  const [inputIntake, setInputIntake] = useState(250);
  const [inputGoal, setInputGoal] = useState(goal);

  const progress = (intake / goal) * 100;

  useEffect(() => {
    const now = new Date();
    const millisUntilMidnight = new Date(
      now.getFullYear(),
      now.getMonth(),
      now.getDate() + 1,
      0,
      0,
      0,
      0
    ).getTime() - now.getTime();

    const timeout = setTimeout(() => {
      setIntake(0);
    }, millisUntilMidnight);

    return () => clearTimeout(timeout);
  }, [intake]);

  const handleGoalDialogOpen = (e) => {
    e.stopPropagation();
    setInputGoal(goal);
    setGoalDialogOpen(true);
  };

  const handleGoalDialogClose = () => {
    setGoalDialogOpen(false);
  };

  const handleIntakeDialogOpen = () => {
    setInputIntake(250);
    setIntakeDialogOpen(true);
  };

  const handleIntakeDialogClose = () => {
    setIntakeDialogOpen(false);
  };

  const handleGoalSubmit = (e) => {
    e.preventDefault();
    if (inputGoal > 0) {
      setGoal(inputGoal);
      if (intake > inputGoal) {
        setIntake(inputGoal);
      }
      setGoalDialogOpen(false);
    }
  };

  const handleIntakeSubmit = (e) => {
    e.preventDefault();
    if (inputIntake > 0) {
      setIntake((prevIntake) => prevIntake + inputIntake);
      setIntakeDialogOpen(false);
    }
  };

  // Calculate the actual angle for the continuous loop effect
  const calculateStrokeDasharray = (value, total) => {
    const circumference = 2 * Math.PI * 16.9155; // SVG circle radius is 16.9155
    const percentage = (value % 100) / 100; // Keep percentage within 100
    return `${circumference * percentage} ${circumference}`;
  };

  const strokeDasharray = calculateStrokeDasharray(progress, 100);

  return (
    <Box>
      <Button
        variant="outlined"
        onClick={handleIntakeDialogOpen}
        sx={{
          width: '200px',
          height: '200px',
          borderRadius: '12px',
          position: 'relative',
          padding: '16px',
          textTransform: 'none',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        <IconButton
          aria-label="settings"
          onClick={handleGoalDialogOpen}
          sx={{
            position: 'absolute',
            top: 8,
            right: 8,
            zIndex: 2,
          }}
        >
          <SettingsIcon />
        </IconButton>

        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            <WaterDropIcon sx={{ color: '#94C1C6', mr: 0 }} />
            <Typography variant="h6" sx={{ color: 'black', textAlign: 'center' }}>
              Water
            </Typography>
          </Box>
          <Box sx={{ position: 'relative', display: 'inline-flex' }}>
            <svg width="90" height="90" viewBox="0 0 36 36">
              <path
                d="M18 1.0845
                a 16.9155 16.9155 0 0 1 0 33.831
                a 16.9155 16.9155 0 0 1 0 -33.831"
                fill="none"
                stroke="#E0E0E0"
                strokeWidth="4" // Adjust thickness here
                strokeLinecap="round"
                style={{ filter: 'drop-shadow(0px 0px 3px #aaa)' }} // Outer shadow
              />
              <path
                d="M18 1.0845
                a 16.9155 16.9155 0 0 1 0 33.831"
                fill="none"
                stroke="#94C1C6"
                strokeWidth="4" // Adjust thickness here
                strokeLinecap="round"
                strokeDasharray={strokeDasharray}
                style={{
                  filter: 'drop-shadow(0px 0px 4px #000)', // Inner shadow
                  transformOrigin: 'center',
                }}
              />
            </svg>
            <Box
              sx={{
                position: 'absolute',
                top: '50%',
                left: '50%',
                transform: 'translate(-50%, -50%)',
              }}
            >
              <GlassOfWaterIcon progress={progress > 100 ? 100 : progress} />
            </Box>
          </Box>
          <Typography variant="body2" sx={{ mt: 1 }}>
            {intake}ml / {goal}ml
          </Typography>
        </Box>
      </Button>

      {/* Dialog for setting daily water goal */}
      <Dialog open={goalDialogOpen} onClose={handleGoalDialogClose}>
        <DialogTitle>
          Set Daily Water Goal
          <IconButton
            aria-label="close"
            onClick={handleGoalDialogClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Daily Water Goal (ml)"
            type="number"
            fullWidth
            value={inputGoal}
            onChange={(e) => setInputGoal(parseInt(e.target.value) || 0)}
            inputProps={{
              step: 50,
              min: 0,
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleGoalDialogClose}>Cancel</Button>
          <Button onClick={handleGoalSubmit}>Set Goal</Button>
        </DialogActions>
      </Dialog>

      {/* Dialog for adding water intake */}
      <Dialog open={intakeDialogOpen} onClose={handleIntakeDialogClose}>
        <DialogTitle>
          Add Water Intake
          <IconButton
            aria-label="close"
            onClick={handleIntakeDialogClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Water Intake (ml)"
            type="number"
            fullWidth
            value={inputIntake}
            onChange={(e) => setInputIntake(parseInt(e.target.value) || 0)}
            inputProps={{
              step: 50,
              min: 0,
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleIntakeDialogClose}>Cancel</Button>
          <Button onClick={handleIntakeSubmit}>Add Intake</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default WaterIntake;
