import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import {
  Box,
  CircularProgress,
  Button,
  Typography,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  IconButton,
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import SettingsIcon from '@mui/icons-material/Settings';

const GlassOfWaterIcon = ({ progress }) => {
  const waterHeight = (100 - progress) / 100 * 20;

  return (
    <svg viewBox="0 0 24 24" style={{ width: '50px', height: '50px', fill: 'none' }}>
      <rect x="7" y="2" width="10" height="20" rx="2" ry="2" stroke="black" strokeWidth="1" fill="none" />
      <rect
        x="7"
        y={2 + 20 - (progress / 100) * 20}
        width="10"
        height={(progress / 100) * 20}
        fill="#94C1C6"
        stroke="none"
      />
    </svg>
  );
};

GlassOfWaterIcon.propTypes = {
  progress: PropTypes.number.isRequired,
};

const WaterIntake = () => {
  const [goal, setGoal] = useState(2000);
  const [intake, setIntake] = useState(0);
  const [goalDialogOpen, setGoalDialogOpen] = useState(false);
  const [intakeDialogOpen, setIntakeDialogOpen] = useState(false);
  const [inputIntake, setInputIntake] = useState(250);
  const [inputGoal, setInputGoal] = useState(goal);

  const progress = Math.min((intake / goal) * 100, 100);

  useEffect(() => {
    const now = new Date();
    const millisUntilMidnight = new Date(
      now.getFullYear(),
      now.getMonth(),
      now.getDate() + 1,
      0,
      0,
      0,
      0
    ).getTime() - now.getTime();

    const timeout = setTimeout(() => {
      setIntake(0);
    }, millisUntilMidnight);

    return () => clearTimeout(timeout);
  }, [intake]);

  const handleGoalDialogOpen = (e) => {
    e.stopPropagation(); // Prevent triggering parent onClick
    setInputGoal(goal);
    setGoalDialogOpen(true);
  };

  const handleGoalDialogClose = () => {
    console.log("Closing Goal Dialog"); // Debugging log
    setGoalDialogOpen(false);
  };

  const handleIntakeDialogOpen = () => {
    console.log("Opening Intake Dialog"); // Debugging log
    setInputIntake(250); // Reset intake input value when opening the dialog
    setIntakeDialogOpen(true);
  };

  const handleIntakeDialogClose = () => {
    console.log("Closing Intake Dialog"); // Debugging log
    setIntakeDialogOpen(false);
  };

  const handleGoalSubmit = (e) => {
    e.preventDefault();
    console.log("Setting Goal:", inputGoal); // Debugging log
    if (inputGoal > 0) {
      setGoal(inputGoal);
      if (intake > inputGoal) {
        setIntake(inputGoal); // Adjust intake if it exceeds the new goal
      }
      setGoalDialogOpen(false); // Close dialog after submitting
    }
  };

  const handleIntakeSubmit = (e) => {
    e.preventDefault();
    console.log("Adding Intake:", inputIntake); // Debugging log
    if (inputIntake > 0) {
      setIntake((prevIntake) => Math.min(prevIntake + inputIntake, goal));
      setIntakeDialogOpen(false); // Close dialog after submitting
    }
  };

  return (
    <Button
      variant="outlined"
      onClick={handleIntakeDialogOpen}
      sx={{
        width: '100%',
        height: '200px',
        borderRadius: '12px',
        position: 'relative',
        padding: '16px',
        textTransform: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      <IconButton
        aria-label="settings"
        onClick={handleGoalDialogOpen}
        sx={{
          position: 'absolute',
          top: 8,
          right: 8,
          zIndex: 2,
        }}
      >
        <SettingsIcon />
      </IconButton>

      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        <Typography variant="h6" sx={{ mb: 1 }}>
          Water
        </Typography>
        <Box sx={{ position: 'relative', display: 'inline-flex' }}>
          <CircularProgress
            variant="determinate"
            value={100}
            size={100}
            thickness={4}
            sx={{ color: '#E0E0E0' }}
          />
          <CircularProgress
            variant="determinate"
            value={progress}
            size={100}
            thickness={4}
            sx={{
              color: '#94C1C6',
              position: 'absolute',
              left: 0,
            }}
          />
          <Box
            sx={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
            }}
          >
            <GlassOfWaterIcon progress={progress} />
          </Box>
        </Box>
        <Typography variant="body2" sx={{ mt: 1 }}>
          {intake}ml / {goal}ml
        </Typography>
      </Box>

      {/* Dialog for setting daily water goal */}
      <Dialog open={goalDialogOpen} onClose={handleGoalDialogClose}>
        <DialogTitle>
          Set Daily Water Goal
          <IconButton
            aria-label="close"
            onClick={handleGoalDialogClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Daily Water Goal (ml)"
            type="number"
            fullWidth
            value={inputGoal}
            onChange={(e) => setInputGoal(parseInt(e.target.value) || 0)}
            inputProps={{
              step: 50,
              min: 0,
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleGoalDialogClose}>Cancel</Button>
          <Button onClick={handleGoalSubmit}>Set Goal</Button>
        </DialogActions>
      </Dialog>

      {/* Dialog for adding water intake */}
      <Dialog open={intakeDialogOpen} onClose={handleIntakeDialogClose}>
        <DialogTitle>
          Add Water Intake
          <IconButton
            aria-label="close"
            onClick={handleIntakeDialogClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Water Intake (ml)"
            type="number"
            fullWidth
            value={inputIntake}
            onChange={(e) => setInputIntake(parseInt(e.target.value) || 0)}
            inputProps={{
              step: 50,
              min: 0,
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleIntakeDialogClose}>Cancel</Button>
          <Button onClick={handleIntakeSubmit}>Add Intake</Button>
        </DialogActions>
      </Dialog>
    </Button>
  );
};

export default WaterIntake;
