import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import {
  Box,
  CircularProgress,
  Button,
  Typography,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  IconButton,
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import SettingsIcon from '@mui/icons-material/Settings';
import WaterDropIcon from '@mui/icons-material/WaterDrop';

const GlassOfWaterIcon = ({ progress }) => {
  return (
    <svg viewBox="0 0 24 24" style={{ width: '40px', height: '40px', fill: 'none' }}>
      <rect x="7" y="2" width="10" height="20" rx="2" ry="2" stroke="black" strokeWidth="1" fill="none" />
      <rect
        x="7"
        y={2 + 20 - (progress / 100) * 20}
        width="10"
        height={(progress / 100) * 20}
        fill="#94C1C6"
        stroke="none"
      />
    </svg>
  );
};

GlassOfWaterIcon.propTypes = {
  progress: PropTypes.number.isRequired,
};

const WaterIntake = () => {
  const [goal, setGoal] = useState(2500);
  const [intake, setIntake] = useState(0);
  const [goalDialogOpen, setGoalDialogOpen] = useState(false);
  const [intakeDialogOpen, setIntakeDialogOpen] = useState(false);
  const [inputIntake, setInputIntake] = useState(250);
  const [inputGoal, setInputGoal] = useState(goal);

  const progress = (intake / goal) * 100;

  useEffect(() => {
    const resetIntakeAtMidnight = () => {
      const now = new Date();
      const millisUntilMidnight = new Date(
        now.getFullYear(),
        now.getMonth(),
        now.getDate() + 1,
        0,
        0,
        0,
        0
      ).getTime() - now.getTime();

      const timeout = setTimeout(() => {
        setIntake(0);
        resetIntakeAtMidnight();
      }, millisUntilMidnight);

      return () => clearTimeout(timeout);
    };

    resetIntakeAtMidnight();
  }, []);

  const handleGoalDialogOpen = (e) => {
    e.stopPropagation();
    setInputGoal(goal);
    setGoalDialogOpen(true);
  };

  const handleGoalDialogClose = () => {
    setGoalDialogOpen(false);
  };

  const handleIntakeDialogOpen = () => {
    setInputIntake(250);
    setIntakeDialogOpen(true);
  };

  const handleIntakeDialogClose = () => {
    setIntakeDialogOpen(false);
  };

  const handleGoalSubmit = (e) => {
    e.preventDefault();
    if (inputGoal > 0) {
      setGoal(inputGoal);
      if (intake > inputGoal) {
        setIntake(inputGoal);
      }
      setGoalDialogOpen(false);
    }
  };

  const handleIntakeSubmit = (e) => {
    e.preventDefault();
    if (inputIntake > 0) {
      setIntake((prevIntake) => prevIntake + inputIntake);
      setIntakeDialogOpen(false);
    }
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        width: '200px',
        height: '200px',
        borderRadius: '12px',
        border: '1px solid #94C1C6',
        padding: '16px',
        position: 'relative',
      }}
      onClick={handleIntakeDialogOpen}
    >
      <IconButton
        aria-label="settings"
        onClick={(e) => {
          e.stopPropagation();
          handleGoalDialogOpen(e);
        }}
        sx={{
          position: 'absolute',
          top: 8,
          right: 8,
        }}
      >
        <SettingsIcon />
      </IconButton>

      <WaterDropIcon sx={{ color: '#94C1C6', fontSize: 20, marginBottom: '4px' }} />
      <Typography variant="h6" sx={{ color: 'black', marginBottom: '8px' }}>
        Water
      </Typography>

      <Box sx={{ position: 'relative', display: 'inline-flex' }}>
        <CircularProgress
          variant="determinate"
          value={Math.min(progress, 100)}
          size={100}
          thickness={6}
          sx={{
            color: '#94C1C6',
          }}
        />
        {progress > 100 && (
          <CircularProgress
            variant="determinate"
            value={progress - 100}
            size={100}
            thickness={6}
            sx={{
              color: '#94C1C6',
              position: 'absolute',
              top: 0,
              left: 0,
              transform: 'rotate(90deg)',
              boxShadow: '0 0 10px rgba(0, 0, 0, 0.3)',
            }}
          />
        )}
        <Box
          sx={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
          }}
        >
          <GlassOfWaterIcon progress={Math.min(progress, 100)} />
        </Box>
      </Box>

      <Typography variant="body2" sx={{ mt: 2, color: '#94C1C6' }}>
        {intake}ml / {goal}ml
      </Typography>

      {/* Dialog for setting daily water goal */}
      <Dialog open={goalDialogOpen} onClose={handleGoalDialogClose}>
        <DialogTitle>
          Set Daily Water Goal
          <IconButton
            aria-label="close"
            onClick={handleGoalDialogClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Daily Water Goal (ml)"
            type="number"
            fullWidth
            value={inputGoal}
            onChange={(e) => setInputGoal(parseInt(e.target.value) || 0)}
            inputProps={{
              step: 50,
              min: 0,
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleGoalDialogClose}>Cancel</Button>
          <Button onClick={handleGoalSubmit}>Set Goal</Button>
        </DialogActions>
      </Dialog>

      {/* Dialog for adding water intake */}
      <Dialog open={intakeDialogOpen} onClose={handleIntakeDialogClose}>
        <DialogTitle>
          Add Water Intake
          <IconButton
            aria-label="close"
            onClick={handleIntakeDialogClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Water Intake (ml)"
            type="number"
            fullWidth
            value={inputIntake}
            onChange={(e) => setInputIntake(parseInt(e.target.value) || 0)}
            inputProps={{
              step: 50,
              min: 0,
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleIntakeDialogClose}>Cancel</Button>
          <Button onClick={handleIntakeSubmit}>Add Intake</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default WaterIntake;
