import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import {
  Box,
  CircularProgress,
  Button,
  Typography,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  IconButton,
  Paper,
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';

const GlassOfWaterIcon = ({ progress }) => {
  const waterHeight = (100 - progress) / 2; // Calculate the water height

  return (
    <svg
      viewBox="0 0 24 24"
      style={{
        width: '40px',
        height: '40px',
        fill: 'none',
        stroke: 'black',
        strokeWidth: '1',
      }}
    >
      <rect x="7" y="2" width="10" height="20" rx="2" ry="2" />
      <rect
        x="7"
        y={`${waterHeight + 12}`}
        width="10"
        height={`${progress / 2}`}
        fill="#94C1C6"
      />
    </svg>
  );
};

GlassOfWaterIcon.propTypes = {
  progress: PropTypes.number.isRequired,
};

const WaterIntake = () => {
  const [goal, setGoal] = useState(2000); // Daily water goal in ml
  const [intake, setIntake] = useState(0); // Current water intake
  const [goalDialogOpen, setGoalDialogOpen] = useState(false);
  const [intakeDialogOpen, setIntakeDialogOpen] = useState(false);
  const [inputIntake, setInputIntake] = useState(50); // Default to 50ml increment/decrement

  const progress = Math.min((intake / goal) * 100, 100);

  // Function to reset intake at midnight
  useEffect(() => {
    const resetAtMidnight = () => {
      const now = new Date();
      const millisUntilMidnight = new Date(
        now.getFullYear(),
        now.getMonth(),
        now.getDate() + 1,
        0,
        0,
        0,
        0
      ).getTime() - now.getTime();
      setTimeout(() => {
        setIntake(0);
        resetAtMidnight();
      }, millisUntilMidnight);
    };
    resetAtMidnight();
  }, []);

  // Handle goal dialog open/close
  const handleGoalDialogOpen = () => {
    setInputIntake(50); // Reset to 50ml default
    setGoalDialogOpen(true);
  };
  const handleGoalDialogClose = () => {
    setGoalDialogOpen(false);
    setInputIntake(50); // Reset to 50ml default
  };

  // Handle intake dialog open/close
  const handleIntakeDialogOpen = () => setIntakeDialogOpen(true);
  const handleIntakeDialogClose = () => {
    setIntakeDialogOpen(false);
    setInputIntake(50); // Reset to 50ml default
  };

  // Handle setting goal
  const handleGoalSubmit = (e) => {
    e.preventDefault();
    setGoal(inputIntake);
    handleGoalDialogClose();
  };

  // Handle adding intake
  const handleIntakeSubmit = (e) => {
    e.preventDefault();
    setIntake((prevIntake) => prevIntake + inputIntake);
    handleIntakeDialogClose();
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        padding: 2,
      }}
    >
      <Paper
        onClick={handleIntakeDialogOpen}
        sx={{
          width: 150,
          height: 150,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          borderRadius: '16px',
          cursor: 'pointer',
          position: 'relative',
        }}
        elevation={3}
      >
        <Typography variant="h6" sx={{ marginBottom: 1 }}>
          Water
        </Typography>
        <Box sx={{ position: 'relative', display: 'inline-flex' }}>
          <CircularProgress
            variant="determinate"
            value={100} // Always show a full circle with a grey background
            size={100}
            thickness={4}
            sx={{
              color: '#E0E0E0', // Grey color for the background
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
            }}
          />
          <CircularProgress
            variant="determinate"
            value={progress} // Dynamic progress overlay
            size={100}
            thickness={4}
            sx={{
              color: '#94C1C6', // Primary color for the progress
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
            }}
          />
          <GlassOfWaterIcon
            progress={progress}
            style={{ cursor: 'pointer', position: 'relative', zIndex: 1 }}
          />
        </Box>
      </Paper>

      {/* Dialog for setting daily water goal */}
      <Dialog open={goalDialogOpen} onClose={handleGoalDialogClose}>
        <DialogTitle>
          Set Daily Water Goal
          <IconButton
            aria-label="close"
            onClick={handleGoalDialogClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Daily Water Goal (ml)"
            type="number"
            fullWidth
            value={inputIntake}
            onChange={(e) => setInputIntake(parseInt(e.target.value))}
            inputProps={{
              step: 50, // Step increment of 50ml
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleGoalDialogClose}>Cancel</Button>
          <Button onClick={handleGoalSubmit}>Set Goal</Button>
        </DialogActions>
      </Dialog>

      {/* Dialog for adding water intake */}
      <Dialog open={intakeDialogOpen} onClose={handleIntakeDialogClose}>
        <DialogTitle>
          Add Water Intake
          <IconButton
            aria-label="close"
            onClick={handleIntakeDialogClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Water Intake (ml)"
            type="number"
            fullWidth
            value={inputIntake}
            onChange={(e) => setInputIntake(parseInt(e.target.value))}
            inputProps={{
              step: 50, // Step increment of 50ml
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleIntakeDialogClose}>Cancel</Button>
          <Button onClick={handleIntakeSubmit}>Add Intake</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default WaterIntake;
