import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import {
  Box,
  CircularProgress,
  Button,
  Typography,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  IconButton,
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import SettingsIcon from '@mui/icons-material/Settings';

const GlassOfWaterIcon = ({ progress }) => {
  // Calculate water level height based on progress
  const waterHeight = (100 - progress) / 100 * 20; // Height in pixels, assuming glass height is 20px

  return (
    <svg
      viewBox="0 0 24 24"
      style={{
        width: '50px',
        height: '50px',
        fill: 'none',
      }}
    >
      {/* Glass outline */}
      <rect x="7" y="2" width="10" height="20" rx="2" ry="2" stroke="black" strokeWidth="1" fill="none" />
      {/* Water level */}
      <rect
        x="7"
        y={2 + 20 - (progress / 100) * 20} // Water level rises with progress
        width="10"
        height={(progress / 100) * 20}
        fill="#94C1C6"
        stroke="none"
      />
    </svg>
  );
};

GlassOfWaterIcon.propTypes = {
  progress: PropTypes.number.isRequired,
};

const WaterIntake = () => {
  const [goal, setGoal] = useState(2000); // Daily water goal in ml
  const [intake, setIntake] = useState(0); // Current water intake
  const [goalDialogOpen, setGoalDialogOpen] = useState(false);
  const [intakeDialogOpen, setIntakeDialogOpen] = useState(false);
  const [inputIntake, setInputIntake] = useState(250); // Default to 250ml increment
  const [inputGoal, setInputGoal] = useState(goal); // For setting new goal

  const progress = Math.min((intake / goal) * 100, 100);

  // Function to reset intake at midnight
  useEffect(() => {
    const now = new Date();
    const millisUntilMidnight = new Date(
      now.getFullYear(),
      now.getMonth(),
      now.getDate() + 1,
      0,
      0,
      0,
      0
    ).getTime() - now.getTime();

    const timeout = setTimeout(() => {
      setIntake(0);
    }, millisUntilMidnight);

    return () => clearTimeout(timeout);
  }, [intake]);

  // Handle goal dialog open/close
  const handleGoalDialogOpen = (e) => {
    e.stopPropagation(); // Prevent triggering parent onClick
    setInputGoal(goal); // Set current goal in input
    setGoalDialogOpen(true);
  };
  const handleGoalDialogClose = () => {
    setGoalDialogOpen(false);
  };

  // Handle intake dialog open/close
  const handleIntakeDialogOpen = () => setIntakeDialogOpen(true);
  const handleIntakeDialogClose = () => {
    setIntakeDialogOpen(false);
    setInputIntake(250); // Reset to 250ml default
  };

  // Handle setting goal
  const handleGoalSubmit = (e) => {
    e.preventDefault();
    if (inputGoal > 0) {
      setGoal(inputGoal);
      if (intake > inputGoal) {
        setIntake(inputGoal); // Adjust intake if it exceeds new goal
      }
      handleGoalDialogClose();
    }
  };

  // Handle adding intake
  const handleIntakeSubmit = (e) => {
    e.preventDefault();
    if (inputIntake > 0) {
      setIntake((prevIntake) => Math.min(prevIntake + inputIntake, goal));
      handleIntakeDialogClose();
    }
  };

  return (
    <Box
      sx={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: 2,
      }}
    >
      <Button
        variant="outlined"
        onClick={handleIntakeDialogOpen}
        sx={{
          width: 200,
          height: 200,
          borderRadius: 4, // Rounded edges
          position: 'relative',
          padding: 2,
          textTransform: 'none',
        }}
      >
        {/* Settings Icon */}
        <IconButton
          aria-label="settings"
          onClick={handleGoalDialogOpen}
          sx={{
            position: 'absolute',
            top: 8,
            right: 8,
            zIndex: 2,
          }}
        >
          <SettingsIcon />
        </IconButton>

        {/* Content inside the button */}
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <Typography variant="h6" sx={{ mb: 1 }}>
            Water
          </Typography>
          <Box sx={{ position: 'relative', display: 'inline-flex' }}>
            <CircularProgress
              variant="determinate"
              value={100} // Full circle background
              size={100}
              thickness={4}
              sx={{
                color: '#E0E0E0',
              }}
            />
            <CircularProgress
              variant="determinate"
              value={progress} // Actual progress
              size={100}
              thickness={4}
              sx={{
                color: '#94C1C6',
                position: 'absolute',
                left: 0,
              }}
            />
            <Box
              sx={{
                position: 'absolute',
                top: '50%',
                left: '50%',
                transform: 'translate(-50%, -50%)',
              }}
            >
              <GlassOfWaterIcon progress={progress} />
            </Box>
          </Box>
          <Typography variant="body2" sx={{ mt: 1 }}>
            {intake}ml / {goal}ml
          </Typography>
        </Box>
      </Button>

      {/* Dialog for setting daily water goal */}
      <Dialog open={goalDialogOpen} onClose={handleGoalDialogClose}>
        <DialogTitle>
          Set Daily Water Goal
          <IconButton
            aria-label="close"
            onClick={handleGoalDialogClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Daily Water Goal (ml)"
            type="number"
            fullWidth
            value={inputGoal}
            onChange={(e) => setInputGoal(parseInt(e.target.value) || 0)}
            inputProps={{
              step: 50, // Step increment of 50ml
              min: 0,
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleGoalDialogClose}>Cancel</Button>
          <Button onClick={handleGoalSubmit}>Set Goal</Button>
        </DialogActions>
      </Dialog>

      {/* Dialog for adding water intake */}
      <Dialog open={intakeDialogOpen} onClose={handleIntakeDialogClose}>
        <DialogTitle>
          Add Water Intake
          <IconButton
            aria-label="close"
            onClick={handleIntakeDialogClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Water Intake (ml)"
            type="number"
            fullWidth
            value={inputIntake}
            onChange={(e) => setInputIntake(parseInt(e.target.value) || 0)}
            inputProps={{
              step: 50, // Step increment of 50ml
              min: 0,
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleIntakeDialogClose}>Cancel</Button>
          <Button onClick={handleIntakeSubmit}>Add Intake</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default WaterIntake;
